import React from 'react';
import styles from '../styles/Item.module.css';

function Item({ block, categoryId, onBlockSelect, used }) {
  const handleClick = () => {
    onBlockSelect({ ...block, categoryId });
  };

  return (
    <button
      className={`${styles.item} ${used ? styles.used : ''}`}
      onClick={handleClick}
    >
      {block.id + 1}
    </button>
  );
}

export default Item;
