import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import QuizPage from './QuizPage';
import QuizCard from './QuizCard';
import Sidebar from './Sidebar';
import styles from '../styles/App.module.css';
import { QuizProvider, useQuizContext } from '../context/QuizContext';
import { loadUniqueUuids } from '../utils/loadJsonData';

function App() {
  const { showQuizPage, setShowQuizPage, setSelectedMode, setCurrentQuizId, selectedMode, currentQuizId } = useQuizContext();
  const [quizData, setQuizData] = useState([]);

  useEffect(() => {
    const uniqueData = loadUniqueUuids(); // Загрузка данных
    setQuizData(uniqueData); // Установка данных в состояние
  }, []);

  const startQuiz = (mode, uuid) => {
	console.log('startQuiz called with mode:', mode, 'and uuid:', uuid); // Новый лог
	setSelectedMode(mode);
	setCurrentQuizId(uuid); // Сохраняем текущий uuid
	setShowQuizPage(true);
	localStorage.setItem('showQuizPage', 'true');
 };
 
  // Этот useEffect добавлен для проверки состояния контекста
  useEffect(() => {
	console.log('showQuizPage:', showQuizPage);
	console.log('selectedMode:', selectedMode);
	console.log('currentQuizId:', currentQuizId);
	if (showQuizPage && selectedMode && currentQuizId) {
	  console.log('All states are set, QuizPage should render.');
	}
 }, [showQuizPage, selectedMode, currentQuizId]);

  return (
	<Router>
	  <div className={styles.container}>
		 {!showQuizPage && <Sidebar />}
		 <div className={showQuizPage ? styles.hidden : styles.content}>
			<Routes>
			  <Route
				 path="/"
				 element={
					!showQuizPage ? (
					  quizData.map((data) => (
						 <QuizCard
							key={data.uuid}
							startQuiz={() => startQuiz(data.mode, data.uuid)}
							mode={data.mode}
							uuid={data.uuid}
							category={data.categories[0]} // Отображаем первую категорию как пример
						 />
					  ))
					) : null
				 }
			  />
			</Routes>
		 </div>
		 {showQuizPage && selectedMode && currentQuizId ? (
			<div className={styles.fullscreen}>
			  <QuizPage quizId={currentQuizId} />
			</div>
		 ) : (
			<div>Loading...</div> // Заменить "No QuizPage rendered" на что-то вроде "Loading..."
		 )}
	  </div>
	</Router>
 );
 
}

// Оборачиваем приложение в провайдер контекста
export default function AppWrapper() {
  return (
    <QuizProvider>
      <App />
    </QuizProvider>
  );
}
