import React, { useState } from 'react';
import QAMode from '../QAMode/QAMode';
import SelectionMode from '../SelectionMode/SelectionMode';
import styles from './Modal.module.css';

const modeComponents = {
  1: QAMode,
  2: SelectionMode,
  // Добавьте другие режимы, если они есть
};

function Modal({
  block,
  categoryName,
  onClose,
  selectedMode,
  onSelectCategory,
}) {
  const ModeComponent = modeComponents[selectedMode];

  const [timerStarted, setTimerStarted] = useState(false);
  const [timerEnded, setTimerEnded] = useState(false);
  const [showAnswer, setShowAnswer] = useState(false);

  const handleTimerEnd = () => {
    setTimerEnded(true);
  };

  const handleShowAnswer = () => {
    setShowAnswer(true);
  };

  const handleForceStop = () => {
    setTimerEnded(true);
  };

  const handleModalClick = (e) => {
    e.stopPropagation(); // предотвращаем закрытие модального окна при клике внутри него
  };

  const handleCloseClick = () => {
    onClose();
  };

  const handleSelectCategoryInternal = (categoryId, blockId) => {
    if (onSelectCategory) {
      onSelectCategory(categoryId, blockId);  // Вызываем переданную функцию для выбора категории
    }
    handleCloseClick();  // Закрываем модальное окно после выбора категории
  };

  if (!block) {
    return null;
  }

  return (
    <div className={`${styles.modal} ${styles.show}`} onClick={handleCloseClick}>
      <div className={styles.modalContent} onClick={handleModalClick}>
        <span className={styles.closeButton} onClick={handleCloseClick}>&times;</span>
        {ModeComponent ? (
          <ModeComponent
            block={block}
            categoryName={categoryName}
            onSelectCategory={handleSelectCategoryInternal}  // Передаем функцию в компонент SelectionMode
            timerStarted={timerStarted}
            setTimerStarted={setTimerStarted}
            timerEnded={timerEnded}
            handleTimerEnd={handleTimerEnd}
            showAnswer={showAnswer}
            handleShowAnswer={handleShowAnswer}
            handleForceStop={handleForceStop}
          />
        ) : (
          <div>Unknown mode</div>
        )}
      </div>
    </div>
  );
}

export default Modal;
