import React, { createContext, useState, useEffect } from 'react';

export const AppContext = createContext();

export const AppProvider = ({ children }) => {
    const [showQuizPage, setShowQuizPage] = useState(() => {
        const savedState = localStorage.getItem('showQuizPage');
        return savedState === 'true';
    });

    const [usedBlocks, setUsedBlocks] = useState(() => {
        const saved = localStorage.getItem('usedBlocks');
        return saved ? JSON.parse(saved) : {};
    });

    useEffect(() => {
        localStorage.setItem('showQuizPage', showQuizPage);
    }, [showQuizPage]);

    useEffect(() => {
        localStorage.setItem('usedBlocks', JSON.stringify(usedBlocks));
    }, [usedBlocks]);

    return (
        <AppContext.Provider
            value={{
                showQuizPage,
                setShowQuizPage,
                usedBlocks,
                setUsedBlocks,
            }}
        >
            {children}
        </AppContext.Provider>
    );
};
