import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import QuizPage from './QuizPage';
import QuizCard from './QuizCard';
import Sidebar from './Sidebar';
import styles from '../styles/App.module.css';
import { loadFileCount } from '../utils/loadData';
import { startQuiz, handleShowQuizPage, handleShowMainMenu, handleNewGame } from '../utils/appUtils';
import { useDispatch } from 'react-redux';

function App() {
  const [showQuizPage, setShowQuizPage] = useState(() => {
    const savedState = localStorage.getItem('showQuizPage');
    return savedState === 'true';
  });
  const [fileCount, setFileCount] = useState(0);
  const dispatch = useDispatch(); // Для возможного использования в будущем

  useEffect(() => {
    const count = loadFileCount();
    setFileCount(count);
  }, []);

  return (
    <Router>
      <div className={styles.container}>
        {!showQuizPage && <Sidebar />}
        <div className={showQuizPage ? styles.hidden : styles.content}>
          <Routes>
            <Route
              path="/"
              element={
                !showQuizPage ? (
                  Array.from({ length: fileCount }).map((_, index) => (
                    <QuizCard
                      key={index}
                      mode={index + 1}
                      startQuiz={() => startQuiz(setShowQuizPage, index + 1)}
                    />
                  ))
                ) : null
              }
            />
            {Array.from({ length: fileCount }).map((_, index) => (
              <Route
                key={index}
                path={`/quiz/${index + 1}`}
                element={
                  <QuizPage
                    showMainMenu={() => handleShowMainMenu(setShowQuizPage)}
                    handleNewGame={() => handleNewGame(setShowQuizPage)}
                  />
                }
              />
            ))}
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
