import React from 'react';
import QAMode from '../QAMode/QAMode';
import SelectionMode from '../SelectionMode/SelectionMode';
import { useQuizContext } from '../../context/QuizContext'; // Импортируем useQuizContext
import styles from './Modal.module.css';

const modeComponents = {
  1: QAMode,
  2: SelectionMode,
};

function Modal({
  block,
  onClose,
  selectedMode,
  timerStarted,
  timerEnded,
  showAnswer,
  setTimerStarted,
  handleTimerEnd,
  handleShowAnswer,
  handleForceStop,
}) {
  const { currentQuizId, markBlockAsUsed } = useQuizContext(); // Получаем currentQuizId и markBlockAsUsed из контекста

  console.log('Modal opened with block:', block);
  const ModeComponent = modeComponents[selectedMode];

  if (!block) {
    return null;
  }

  const handleSelectCategory = () => {
    markBlockAsUsed(currentQuizId, block.categoryId, block.id); // Используем currentQuizId
    onClose();
  };

  return (
    <div className={`${styles.modal} ${styles.show}`} onClick={onClose}>
      <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
        <span className={styles.closeButton} onClick={onClose}>&times;</span>
        {ModeComponent ? (
          <ModeComponent
            block={block}
            showAnswer={showAnswer}
            setTimerStarted={setTimerStarted}
            timerStarted={timerStarted}
            timerEnded={timerEnded}
            handleTimerEnd={handleTimerEnd}
            handleShowAnswer={handleShowAnswer}
            handleForceStop={handleForceStop}
            handleSelectCategory={handleSelectCategory}
          />
        ) : (
          <div>Unknown mode</div>
        )}
      </div>
    </div>
  );
}

export default Modal;
