import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Sidebar from './Sidebar';
import QuizPage from './QuizPage';
import QuizCard from './QuizCard';
import styles from '../styles/App.module.css';
import { loadFileCount, loadJsonDataFiles } from '../utils/loadData';
import { handleShowQuizPage, handleShowMainMenu, handleNewGame } from '../utils/appUtils';

function App() {
    const [fileCount, setFileCount] = useState(0);
    const [quizData, setQuizData] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const count = loadFileCount();
        setFileCount(count);

        const dataFiles = loadJsonDataFiles(); // Загружаем все файлы данных
        setQuizData(dataFiles); // Сохраняем их в состоянии
    }, []);

    const startQuiz = (uuid) => {
        handleShowQuizPage();
        navigate(`/quiz/${uuid}`);
    };

    return (
        <div className={styles.container}>
            <Sidebar />
            <div className={styles.content}>
                <Routes>
                    <Route
                        path="/"
                        element={quizData.map((quiz) => (
                            <QuizCard
                                key={quiz.uuid}
                                startQuiz={() => startQuiz(quiz.uuid)} // Начинаем викторину с соответствующим UUID
                                uuid={quiz.uuid} // Передаем UUID в QuizCard
                            />
                        ))}
                    />
                    <Route
                        path="/quiz/:uuid"
                        element={<QuizPage />}
                    />
                </Routes>
            </div>
        </div>
    );
}

export default function AppWrapper() {
    return (
        <Router>
            <Routes>
                <Route path="/*" element={<App />} />
            </Routes>
        </Router>
    );
}
